INSERT INTO `iv36_Pages` (`Id`, `Description`, `Head`, `Content`) VALUES (1, 'Главное', 'a:5:{s:5:"title";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:6:"author";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:11:"description";s:7:"Главное";s:8:"keywords";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:9:"copyright";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";}', '&lt;h1&gt;Корневой раздел сайта&lt;/h1&gt;'),
(2, 'Визитка', 'a:5:{s:5:"title";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:6:"author";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:11:"description";s:7:"Визитка";s:8:"keywords";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:9:"copyright";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";}', '&lt;center&gt;\r\n\r\n&lt;img src=&quot;./files/common/school.jpg&quot; alt=&quot;Любимая школа!&quot; title=&quot;Любимая школа!&quot; /&gt;\r\n\r\n&lt;/center&gt;\r\n\r\n&lt;br /&gt;\r\n&lt;hr /&gt;\r\n\r\n&lt;b&gt;\r\n   Адрес:&lt;br /&gt;\r\n   153048,&lt;br /&gt;\r\n   г. Иваново,&lt;br /&gt;\r\n   ул. Ген. Хлебникова, д.32&lt;br /&gt;\r\n   (4932) 23-63-36&lt;br /&gt;\r\n   e-mail: School36@ivedu.ru&lt;br /&gt;\r\n&lt;/b&gt;\r\n\r\n&lt;hr /&gt;\r\n\r\n&lt;center&gt;\r\n\r\n&lt;img src=&quot;./files/iv36/common/img_9879_direktor.jpg&quot; alt=&quot;Директор школы&quot; title=&quot;Директор школы&quot; /&gt;\r\n\r\n&lt;/center&gt;\r\n\r\n&lt;h2&gt;Директор гимназии №36 Земерова Антонина Георгиевна &lt;/h2&gt;\r\n\r\n&lt;p&gt;\r\nС 1995г. директор МОУ-Гимназии №36,\r\nшколы с углубленным изучением\r\nискусства и гуманитарных наук.\r\n&lt;/p&gt;\r\n\r\n&lt;p&gt;\r\nВ 1998г. гимназия под ее руководством\r\nво второй раз завоевывает\r\nзвание «Школа года»,\r\nа в 2003 г. награждена\r\nпремией «Престиж»\r\nв номинации «Развитие детского творчества».\r\n&lt;/p&gt;\r\n\r\n&lt;p&gt;\r\nВ 2001г. избрана координатором\r\nСовета директоров Ленинского\r\nобразовательного округа,\r\nа в 2006 году - председатель\r\nСовета директоров ОУ Ивановской области.\r\n&lt;/p&gt;\r\n\r\n&lt;p&gt;\r\nВ 2004 г. присвоено \r\nзвание «Заслуженный учитель РФ».\r\n&lt;/p&gt;\r\n\r\n&lt;p&gt;\r\nВ 2006 г.Гимназия №36 - победитель\r\nконкурса ОУ активно\r\nвнедряющих ИТ.\r\n&lt;/p&gt;\r\n\r\n&lt;p&gt;\r\n В 2006 году школа стала победителем в конкурсе ПНПО.\r\n\r\n     Грант направлен на приобретение современной компьютерной,\r\n     множительной и мультимедийной техники,\r\n     а также оборудования для учебных кабинетов.\r\n\r\n&lt;/p&gt; \r\n\r\n &lt;b&gt;\r\n&lt;p&gt;\r\n В 2008 году школа стала победителем в конкурсе ПНПО.\r\n  \r\n     Грант направлен на оснащение предметных кабинетов современной техникой. А также на оформление школьного Музея.\r\n \r\n&lt;/p&gt; \r\n&lt;p&gt;\r\nВ 2008 г. директор Антонина Георгиевна награждена\r\nпремией «Престиж»\r\nв номинации «Лидер образования».\r\n&lt;/p&gt;\r\n  &lt;/b&gt;\r\n'),
(3, 'Программа развития', '', '<h1>Корневой раздел сайта</h1>'),
(4, 'Модель развития личности', 'a:5:{s:5:"title";s:24:"Модель развития личности";s:6:"author";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:11:"description";s:24:"Модель развития личности";s:8:"keywords";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:9:"copyright";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";}', '&lt;p&gt;\r\nАвторская модель непрерывного\r\nдуховно-нравственного и\r\nхудожественно-эстетического\r\nобразования в системе\r\nразвития МОУ-гимназии № 36\r\n&lt;/p&gt;\r\n\r\n&lt;h2&gt;\r\nТЕМА ИННОВАЦИОННОЙ ДЕЯТЕЛЬНОСТИ\r\nМУНИЦИПАЛЬНОЙ ОПОРНОЙ ПЛОЩАДКИ\r\n&lt;/h2&gt;\r\n\r\n&lt;p&gt;\r\nУправление качеством современного школьного образования\r\n&lt;/p&gt;\r\n\r\n&lt;h2&gt;ОСНОВНЫЕ НАПРАВЛЕНИЯ ИННОВАЦИОННОЙ ДЕЯТЕЛЬНОСТИ&lt;/h2&gt;\r\n\r\n&lt;ul&gt;\r\n\r\n&lt;li&gt;\r\nРазработка и внедрение\r\nМодели управления качеством\r\nдуховно-нравственного\r\nи эстетического образования\r\nв гуманитарной гимназии\r\n&lt;/li&gt;\r\n\r\n&lt;li&gt;\r\nРеализация потенциальных возможностей,\r\nпрограмм: «Духовно –\r\nнравственного образования»,\r\n«Педагогика здоровья»,\r\n«Одарённые дети»\r\n&lt;/li&gt;\r\n\r\n\r\n&lt;li&gt;\r\nФормирование социокультурной\r\nсреды гуманитарной гимназии,\r\nспособствующей духовно- нравственному\r\nразвитию и образованию учащихся \r\nи педагогов гимназии\r\n&lt;/li&gt;\r\n\r\n\r\n&lt;li&gt;\r\nФормирование ценности\r\nздорового образа жизни\r\nв системе жизнедеятельности гимназии\r\n&lt;/li&gt;\r\n\r\n&lt;/ul&gt;\r\n\r\n&lt;h2&gt;\r\nРеализация Программы информатизации образовательного процесса\r\n&lt;/h2&gt;\r\n\r\n&lt;p&gt;\r\nВ гимназии создана уникальная социо-культурная среда,\r\nкоторая способствует не только повышению\r\nкачества обучения, но и создает\r\nблагоприятные условия для\r\nдуховно-нравственного\r\nи эстетического развития школьников,\r\nличностного и профессионального\r\nроста педагогов.\r\n&lt;/p&gt;\r\n\r\n&lt;h2&gt;\r\nТворческие коллективы\r\n&lt;/h2&gt;\r\n\r\n&lt;p&gt;\r\nТворческие коллективы гимназии №36\r\nявляются участниками всех школьных\r\nпраздников, а также городских,\r\nобластных и межрегиональных конкурсов\r\nи фестивалей, имеют грамоты, дипломы\r\nи  награды победителей.\r\n&lt;/p&gt;\r\n'),
(5, 'Партнёры дополнительного образования', 'a:5:{s:5:"title";s:36:"Партнёры дополнительного образования";s:6:"author";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:11:"description";s:36:"Партнёры дополнительного образования";s:8:"keywords";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:9:"copyright";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";}', '&lt;p&gt;\r\nЭффективности реализации\r\nПрограммы развития\r\nспособствует тесное\r\nи плодотворное сотрудничество\r\nс партнёрами гимназии.\r\n&lt;/p&gt;\r\n\r\n&lt;h2&gt;\r\nПартнёры МОУ-гимназии №36\r\nв организации дополнительного\r\nобразования детей\r\n&lt;/h2&gt;\r\n\r\n&lt;center&gt;\r\n\r\n&lt;img src=&quot;./files/iv36/common/partner.gif&quot;\r\n  alt=&quot;Партнёры МОУ-гимназии №36 в организации дополнительного\r\n  образования детей&quot;\r\n  title=&quot;Партнёры МОУ-гимназии №36 в организации\r\n  дополнительного образования детей&quot;\r\n/&gt;\r\n\r\n&lt;/center&gt;\r\n\r\n&lt;h2&gt;Охват учащихся дополнительным образованием&lt;/h2&gt;\r\n\r\n&lt;br /&gt;\r\n\r\n1. &lt;a href=&quot;./files/common/Оhvat_uch_cya_dop_obrazovaniem.doc&quot;&gt;Охват учащихся дополнительным образованием (таблица) [MS Word]&lt;/a&gt;\r\n\r\n&lt;br /&gt;\r\n\r\n2. &lt;a href=&quot;./files/common/Уroven__vocpitannocti.doc&quot;&gt;Уровень воспитанности учащихсяся (2 гистограммы) [MS Word]&lt;/a&gt;'),
(6, 'Программа информатизации', 'a:5:{s:5:"title";s:24:"Программа информатизации";s:6:"author";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:11:"description";s:24:"Программа информатизации";s:8:"keywords";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:9:"copyright";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";}', '&lt;p&gt;\r\nВ соответствии с Программой информатизации\r\nв образовательном процессе всё чаще\r\nиспользуются информационные технологии.\r\n&lt;/p&gt;\r\n\r\n&lt;center&gt;\r\n&lt;img src=&quot;./files/common/cpu1.jpg&quot; alt=&quot;Ученик за компьютером&quot; title=&quot;Ученик за компьютером&quot; /&gt;\r\n&lt;/center&gt;\r\n\r\n\r\n\r\n&lt;p&gt;\r\nНовые компьютерные программы позволяют\r\nучащимся  создавать творческие проекты,\r\nзащищать их на научно-практических\r\nконференциях, конкурсах, выставках.\r\n&lt;/p&gt;\r\n\r\n&lt;h3&gt;Творческие проекты&lt;/h3&gt;\r\n\r\n1. &lt;a href=&quot;./files/common/kakie_byvaut_givotnye.doc&quot;&gt;Какие бывают животные (Автор: Пыльнов Ваня) [MS Word]&lt;/a&gt;\r\n\r\n&lt;br /&gt;\r\n\r\n2. &lt;a href=&quot;./files/common/tzep__pitaniya.doc&quot;&gt;Цепь питания (MS Word)&lt;/a&gt;\r\n\r\n'),
(7, 'Программа - Здоровье', 'a:5:{s:5:"title";s:20:"Программа - Здоровье";s:6:"author";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:11:"description";s:20:"Программа - Здоровье";s:8:"keywords";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";s:9:"copyright";s:69:"Муниципальное общеобразовательное учреждение гимназия № 36, г.Иваново";}', '&lt;p&gt;\r\nПриоритетным в образовательном учреждении\r\nявляется формирование ценности здорового\r\nобраза жизни, своеобразной «идеологии здоровья».\r\nС изменением всего уклада шко¬лы в направлении\r\nздоровья можно ожидать реальных результатов\r\nв изменении отношения ученика к собственному\r\nздоровью.\r\n&lt;/p&gt;\r\n\r\n&lt;center&gt;\r\n&lt;img src=&quot;./files/common/sport_zal.jpg&quot; alt=&quot;Спортивный зал&quot; title=&quot;Спортивный зал&quot; /&gt;\r\n&lt;/center&gt;\r\n\r\n\r\n&lt;h2&gt;\r\nОсновные направления деятельности:\r\n&lt;/h2&gt;\r\n\r\n&lt;ul&gt;\r\n&lt;li&gt;\r\nОтработка моделей (вариантов)\r\nоздоровительных и реабилитационных \r\nмедико-психолого-педагогических мероприятий\r\nв условиях образовательного учреждения\r\nи изучение их эффективности\r\n&lt;/li&gt;\r\n\r\n&lt;li&gt;\r\nОрганизационная и методическая\r\nдеятельность, укрепление\r\nматериальной базы.\r\n&lt;/li&gt;\r\n\r\n&lt;li&gt;\r\nПриведение образовательного процесса\r\nв соответствие с возрастными\r\nи функциональными особенностями\r\nразвития детей\r\n&lt;/li&gt;\r\n\r\n&lt;li&gt;\r\nВнедрение оптимальной системы\r\nмедицинского и психологического\r\nсопровождения образовательного\r\nпроцесса\r\n&lt;/li&gt;\r\n&lt;/ul&gt;'),
