INSERT INTO `iv36_Pub_Msg` (`Id`, `Content`, `Display`) VALUES (11, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:14:"Директор школы";s:5:"topic";s:31:"Детский городок у гимназии №36.";s:3:"ask";s:125:"При В.Б. Мигаеве по периметру нашей школы существовал городок. почему он разрушен какие меры применяются по его востанолению.";s:3:"ans";s:335:"Городок был хороший. Но к сожалению, жильцы близ лежайшего частного сектора посчитали его своей собственностью. В следствие чего, городок по дощечке переехал в частные руки наших соседей. В сегодняшних условиях на детские площадки школам денег не дают. Когда и при каких обстоятельствах начнется восстановление городка сказать трудно. ";}', 1),
(5, 'a:5:{s:4:"from";s:7:"голубев";s:2:"to";s:14:"Директор школы";s:5:"topic";s:5:"уроки";s:3:"ask";s:32:"Когда в школу детям на уроки\r\n\r\n";s:3:"ans";s:101:"Уважаемые родители, каникулы заканчиваются 2 апреля. Учебный процесс начинается с 3 апреля (четверг).";}', 1),
(8, 'a:5:{s:4:"from";s:11:"СЛАВА ПАНОВ";s:2:"to";s:14:"Директор школы";s:5:"topic";s:7:"занятия";s:3:"ask";s:103:"Скажите пожалуйста в какие дни мы не учимся из за ЕГЭ ? С какого числа у нас каникулы? ЗАРАНИЕ СПАСИБО.";s:3:"ans";s:171:"В связи с проведением ЕГЭ на базе нашей школы 22, 26 и 29 мая неучебные дни.\r\nКаникулы начинаются:\r\n11 классы - с 15 мая,\r\n1, 9 классы - с 24 мая,\r\n2-8,10 классы с 31 мая.";}', 1),
(9, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:14:"Директор школы";s:5:"topic";s:7:"Экзамен";s:3:"ask";s:69:"Когда можно узнать результаты экзамена по алгебре за 9 класс?или где?";s:3:"ans";s:164:"Результаты экзаменов за 9 класс в новой форме (ЕГЭ) можно узнать с 19 июня у классного руководителя или у заместителя директора по УВР Курепкиной Лилии Владимировны";}', 1),
(10, 'a:5:{s:4:"from";s:13:"Ваня Мазанов ";s:2:"to";s:14:"Директор школы";s:5:"topic";s:10:"1 сентября";s:3:"ask";s:42:"Какие меры по нашей охране вы придпримите.";s:3:"ans";s:214:"Во-первых, для охраны школьного контингента в школе работает служба охраны.\r\nВо-вторых, 1 сентября, как и в другие общешкольные праздники организовано дополнительное дежурство учителе, родителей и служб города УВД.";}', 1),
(6, 'a:5:{s:4:"from";s:13:"михайлов глеб";s:2:"to";s:14:"Директор школы";s:5:"topic";s:5:"уклон";s:3:"ask";s:27:"с каким у нас школа уклоном";s:3:"ans";s:69:"Наша гимназия работает с углубленным изучением области обществознания";}', 1),
(7, 'a:5:{s:4:"from";s:16:"Антон Нестеренко";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:0:"";s:3:"ask";s:22:"А когда форму отменят?";s:3:"ans";s:213:"К сожалению форму отменили еще в 90-х годах, прошлого века. Сегодня мы стараемся привить вам вкус в выборе одежды. А именно, на работу принято носить деловой стиль одежды. Рекомендуемый  стиль в одежде организует.";}', 1),
(35, 'a:5:{s:4:"from";s:13:"Женя Воропаев";s:2:"to";s:14:"Директор школы";s:5:"topic";s:5:"музей";s:3:"ask";s:55:"АНТОНИНА ГЕОРГИЕВНА ,КОГДА ЗАСЕДАНИЕ ШОКОЛЬНОГО СОВЕТА?";s:3:"ans";s:89:"По плану работы Управляющего Совета следующее заседание состоится в мае. Дата уточняется.";}', 1),
(12, 'a:5:{s:4:"from";s:14:"Жени Воропаева";s:2:"to";s:14:"Директор школы";s:5:"topic";s:5:"музей";s:3:"ask";s:58:"Скажите пожалуйста есть ли в активе музея ребята7б класса.";s:3:"ans";s:131:"В активе музея занимается много ребят, но это учащиеся 7в и 8г классов. Но двери наши открыты, мы приглашаем всех желающих творить.";}', 1),
(13, 'a:5:{s:4:"from";s:6:"Крилла";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:29:"результаты экзаменов (когда?)";s:3:"ask";s:95:"Здравствуйте!!!\r\nКогда будут известны результаты ЕГЭ по алгебре и русскому языку у 9-х классов?";s:3:"ans";s:34:"Результаты ЕГЭ известны с 19 июня.";}', 1),
(14, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:46:"Заместитель директора по воспитательной работе";s:5:"topic";s:21:"Внешний вид школьника";s:3:"ask";s:65:"Какие требования предьявляются к внешнему виду и одежде школьника";s:3:"ans";s:220:"Согласно Уставу Гимназии и Положению о внешнем виде учащихся,  гимназист придерживается делового стиля одежды (костюм, жакет), кроме того должен быть чистым, аккуратным, волосы уложены. Бижутерия и косметика отсутствует.";}', 1),
(20, 'a:5:{s:4:"from";s:24:"Хор &quot; Рассвет&quot;";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:69:"Выступление хора на том же фестивале что и &quot;Лёгкое дыхание&quot;";s:3:"ask";s:174:"Мы, хор &quot;Рассвет&quot; 18 октября с коллективом &quot;Лёкое дыхание&quot; ездили в город Москва на фестиваль &quot;Любимый город&quot; и стали там луриатами 3 степени.\r\n";s:3:"ans";s:47:"Мы вас от всей души поздравляем с этой победой!";}', 1),
(21, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:22:"Школа &quot;Гном&quot;";s:3:"ask";s:44:"Как можно поступить в школу &quot;Гном&quot;";s:3:"ans";s:166:"Если Вы хотите поступить  в школу &quot;ГНОМ&quot; на 2009 - 2010 учебный год, то Вы должны подать заявление о приеме. Прием заявлений начнется с 1 декабря 2008 года.";}', 1),
(18, 'a:5:{s:4:"from";s:18:"семья Гайбуллаевых";s:2:"to";s:14:"Директор школы";s:5:"topic";s:12:"первый класс";s:3:"ask";s:190:"Здравствуйте! Ответьте,пожалуйста,в 2009 году старшая (из трёх) дочь пойдёт в первый класс.Каким образом (по каким правилам) можно поступить в Вашу школу ? С уважением семья Гайбуллаевых .\r\n";s:3:"ans";s:381:"Здравствуйте! В соответствии с приказом Управления образования администарции г.Иваново №63 от 12.02.2008г. &quot;Об организации приема детей в 1 класс&quot; набор детей в 1-е классы будет объявлен за две недели до начала приема документов, но не позднее 1 марта 2009 г. Дополнительная информация будет в объявлении о наборе детей в 1-е классы.\r\nСледите за новостями на нашем сайте.";}', 1),
(19, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:12:"Укажите тему";s:3:"ask";s:61:"Почему вы не написали про музыкальную школу? Про хор Рассвет?";s:3:"ans";s:108:"Уточните, пожалуйста, вопрос. Что именно мы должны написать про музыкальную школу и хор &quot;Рассвет&quot;.";}', 1),
(22, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:14:"Директор школы";s:5:"topic";s:21:"поступление в 1 класс";s:3:"ask";s:126:"Здраствуйте, скажите пожайлуста что необходимо для поступления в гимназию? нужно ли заранее записываться есть ли собеседование";s:3:"ans";s:61:"Здравствуйте. На ваш вопрос посмотрите, пожалуйста, ответ №18";}', 1),
(23, 'a:5:{s:4:"from";s:21:"elena78sm2008@mail.ru";s:2:"to";s:14:"Директор школы";s:5:"topic";s:26:"устройство ребенка в школу";s:3:"ask";s:114:"Хочу чтобы мой сын учился в вашей школе. Что для этого необходимо предпринять? Как осуществляется прием в 1 класс?";s:3:"ans";s:380:"Здравствуйте! В соответствии с приказом Управления образования администарции г.Иваново №63 от 12.02.2008г. &quot;Об организации приема детей в 1 класс&quot; набор детей в 1-е классы будет объявлен за две недели до начала приема документов, но не позднее 1 марта 2009 г. Дополнительная информация будет в объявлении о наборе детей в 1-е классы. Следите за новостями на нашем сайте.";}', 1),
(24, 'a:5:{s:4:"from";s:15:"Мамихин Василий";s:2:"to";s:14:"Директор школы";s:5:"topic";s:3:"егэ";s:3:"ask";s:81:"ходят слухи что вводят два новых экзамена информатика и английский правда ли это?";s:3:"ans";s:149:"Действительно, вводят два экзамена (английский и информатику) при поступлении в ВУЗ. Подробности об этом Вы сможете узнать в приемной камиссии ВУЗов.";}', 1),
(25, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:14:"Директор школы";s:5:"topic";s:12:"Укажите тему";s:3:"ask";s:245:"Уважаемая Антонина Георгиевна !!! От всей души поздравляем вас с наступающим Новым годом.\r\n                      ученицы 7бкласса                                                                                                                    ";s:3:"ans";s:135:"Спасибо! Вас тоже с наступающими праздниками - Новым годом и Рождеством. Пусть исполнятся все ваши желания, здоровья и успехов в учебе!";}', 1),
(26, 'a:5:{s:4:"from";s:1:"я";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:11:"регистрация";s:3:"ask";s:25:"КАК зарегистрироваца?????";s:3:"ans";s:55:"Уточните, пожалуйста, где Вы хотите зарегистрироваться?";}', 1),
(27, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:21:"Интеллектуальные игры";s:3:"ask";s:98:"Здравствуйте, скажите пожалуйста, когда будут известны результаты игры. Они проходили 24.12.2008. ";s:3:"ans";s:115:"Здравствуйте. Результаты интеллектуальных игр будут известны в течение января. Следите за новостями на нашем сайте.";}', 1),
(28, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:14:"Директор школы";s:5:"topic";s:21:"ПОСТУПЛЕНИЕ В 1 КЛАСС";s:3:"ask";s:150:"Здравствуйте ! Вы не ответили: будет ли собеседование при приёме в 1 класс и,как оно осуществляется,какие требования сейчас ?Заранее спасибо за ответ.";s:3:"ans";s:313:"Здравствуйте. Собеседование проводится, но не для определения приема ребенка в школу, а для распределения по классам.  Требования: возраст ребенка на февраль 2009 года должен быть 6 лет. Выбирая школу с повышенным статусом Вы должны осознавать, что учебная программа сложная и требования к ребенку будут высокие. ";}', 1),
(29, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:4:"ФОТО";s:3:"ask";s:62:"ГДЕ МОЖНО ПОСМОТРЕТЬ ФОТОГРАФФИИ РАЗНЫХ КЛАССОВ СБОРНЫЕ?????\r\n";s:3:"ans";s:48:"Уточните вопрос, какие фотографии вам интересны.";}', 1),
(30, 'a:5:{s:4:"from";s:4:"Юрий";s:2:"to";s:14:"Директор школы";s:5:"topic";s:31:"Перевод ребенка в Вашу Гимназию";s:3:"ask";s:851:"Уважаемая Антонина Георгиевна!\r\nМоя дочь заканчивает начальную школу (4-й класс), одновременно, так сложилось, наша семья меняет место жительства и переезжает в близлежащий к Вашей Гимназии район.\r\nПринимая во внимание положительные отзывы о Вашем учебном учреждении, ознакомившись с информацией на сайте, мне бы хотелось устроить дочь в Вашу гимназию с 2009-2010 учебного года.\r\nКоротко о ребенке. Моя дочь отличница, имеет множество грамот как за отличную учебу, так и за участие в различных конкурсах и в общественной жизни. \r\nКоротко о семье. Я и моя жена имеем высшее образование, работу в известных престижных организациях и хорошие перспективы.\r\nАнтонина Георгиевна, прошу Вас рассмотреть возможность приема моей дочери в Вашу гимназию и ,по возможности, назначить дату и время према по вышеизложенному мной вопросу! \r\nС уважением,\r\nЮ.В. Рубцов";s:3:"ans";s:210:"Здравствуйте! \r\nНа данный момент в 4-х классах  есть 2 вакантных места. Вы можете подойти в день приема директором (понедельник с 13.30 до 18.00) и обговорить решение данной проблемы.\r\nС уважением А.Г.Земерова.";}', 1),
(31, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:12:"Укажите тему";s:3:"ask";s:59:"Хранятся ли у вас видеосъёмки выпускных прошлых лет (1994)?";s:3:"ans";s:54:"В Гимназии имеется свой видеоархив. Начат с 1995 года.";}', 1),
(32, 'a:5:{s:4:"from";s:4:"Юрий";s:2:"to";s:14:"Директор школы";s:5:"topic";s:11:"О приятном!";s:3:"ask";s:369:"О приятном!\r\nАнтонина Георгиевна! Поздравляю Вас и женский коллектив Гимназии с Праздником Весны и красоты!!!\r\nО необходимом!\r\nПрошу сообщить, каким образом будет производиться закупка учебников и прочей литературы для учеников 5-х классов. При организованной закупке сообщите, пожалуйста, сумму и дату приема средств, необходимых для этого.\r\nС уважением,\r\nЮрий Рубцов.";s:3:"ans";s:138:"Спасибо за поздравление. Учебники закупаются организованно. За полной информацией о закупках вам нужно обратится к классному руководителю.";}', 1),
(33, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:9:"Каникулы.";s:3:"ask";s:113:"Добрый вечер! Подскажите, пожалуйста, когда в этом году учащиеся 11 классов заканчивают учиться? Заранее спасибо.";s:3:"ans";s:47:"Учащиеся 11-х классов заканчивают учится 22 мая";}', 1),
(34, 'a:5:{s:4:"from";s:33:"Родительский комитет 11-х классов";s:2:"to";s:46:"Заместитель директора по воспитательной работе";s:5:"topic";s:16:"Последний звонок";s:3:"ask";s:165:"Просим уточнить точную дату и время проведения &quot;последнего звонка&quot; 11-х классов. Это необходимо знать для украшения актового зала и проведения видеосъёмки.";s:3:"ans";s:275:"Последний звонок (школьный) состоится 22 мая в 12 часов. Городской Последний звонок (обязателен для всех выпускников) состоится 23 мая в спорткомплексе &quot;Олимпия&quot;.\r\nВам рекомендую подойти ко мне, Скворцовой Любови Александровне, для решения организационных вопросов.";}', 1),
(36, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:14:"Директор школы";s:5:"topic";s:20:"поступление в 1класс";s:3:"ask";s:183:"Скажите, пожалуйста, если ребенок прописан на ул. Ген.Хлебникова, можем ли мы рассчитывать на поступление в 1 класс в школу №36?\r\nИли данной район приклеплен к другой школе?\r\nСпасибо.";s:3:"ans";s:141:"Проживание и место прописки ребенка на выбор учебного заведения в данном случае не влияет. За нашей гимназией нет закрепленного микрорайона. ";}', 1),
(37, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:16:"Последний звонок";s:3:"ask";s:90:"Здравствуйте. Скажите, когда в этом году последний звонок у 11-х классов? Заранее спасибо.";s:3:"ans";s:53:"Последний звонок (школьный) 22 мая, городской 23 мая.";}', 1),
(38, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:10:"1-ый класс";s:3:"ask";s:101:"Добрый день! Какие требования предъявляемые к первокласснику?\r\nчто он должен знать и уметь?\r\nСпасибо.";s:3:"ans";s:253:"Современная школа предъявляет к ребенку много требований.\r\nСамое главное на момент начала обучения Вашему ребенку должно быть 6,5 лет. \r\nЗнать и уметь что-то особенное не обязательно. Если Ваш ребенок развит, то ему проще будет познавать науки в школе. ";}', 1),
(40, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:14:"Директор школы";s:5:"topic";s:12:"Укажите тему";s:3:"ask";s:91:"Здравствуйте.Скажите пожалуйста, когда у учеников 9 классов первый экзамен.Заранее спасибо.";s:3:"ans";s:55:"Первый экзамен в 9 классах будет русский язык - 27 мая.";}', 1),
(41, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:22:"подготовительные курсы";s:3:"ask";s:100:"Здравствуйте !Когда можно записать ребенка в школу &quot;Гном&quot;, за 1 или за 2 года до 1 класса?";s:3:"ans";s:111:"Запись в школу &quot;ГНОМ&quot; проходит уже сейчас. Занятия годичные, проходят перед поступлением в 1-й класс.";}', 1),
(42, 'a:5:{s:4:"from";s:16:"мама дошкольника";s:2:"to";s:46:"Заместитель директора по воспитательной работе";s:5:"topic";s:19:"школа развития ГНОМ";s:3:"ask";s:279:"Любовь Александровна, подскажите :\r\n1. с какого возраста можно записаться в ГНОМ? в августе ребенку будет 5 лет, можно записаться уже сейчас?\r\n2. к кому и в какое время нужно подойти родителю для доп. консультации и записи?\r\n3.какие документы иметь при себе для записи?\r\nСпасибо!";s:3:"ans";s:309:"Здравствуйте, уважаемые родители!\r\nЕсли Вы планируете поступление ребенка в 1-й класс в следующем году, то в ГНОМ надо записываться уже сейчас. Подойти необходимо к секретарю Ивченко Елене Борисовне или позвонить ей по тел. 23-63-36. Вас с ребенком запишут на собеседование и сообщат о необходимых документах.";}', 1),
(43, 'a:5:{s:4:"from";s:33:"родительский комитет 11-х классов";s:2:"to";s:14:"Директор школы";s:5:"topic";s:16:"Последний звонок";s:3:"ask";s:77:"Просим уточнить дату, время и место проведения фуршета 11-х классов, спасибо.";s:3:"ans";s:107:"О каком фуршете идет речь? Школа не организует детям фуршетов, даже 11-классникам. Точнее задавайте вопрос.";}', 1),
(44, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:12:"Укажите тему";s:3:"ask";s:164:"Дорогая, любимая 36-я гимназия!Все-все-все педагоги, ученики!!! Шлю вам огромный привет и говорю, что очень скучаю по школе.Всех люблю и обнимаю! от Маши Колпаковой";s:3:"ans";s:91:"Спасибо, Мария за теплые слова. Твои чувства взаимны. Надеемся на скорую встречу 24 апреля.";}', 1),
(45, 'a:5:{s:4:"from";s:15:"4 &quot;в&quot;";s:2:"to";s:46:"Заместитель директора по воспитательной работе";s:5:"topic";s:7:"о школе";s:3:"ask";s:56:"мы очень любим эту школу и очень рады , что здесь учимся";s:3:"ans";s:30:"Спасибо. Школа тоже вас любит!";}', 1),
(46, 'a:5:{s:4:"from";s:9:"учащегося";s:2:"to";s:21:"Администратор портала";s:5:"topic";s:3:"ГИА";s:3:"ask";s:61:"Через сколько дней могут быть готовы результаты тестирования?";s:3:"ans";s:72:"Какое тестирование Вы имеете ввиду?Кто Вас тестировал?По какому вопросу?";}', 1),
(47, 'a:5:{s:4:"from";s:11:"Неизвестный";s:2:"to";s:14:"Директор школы";s:5:"topic";s:22:"Группа продленного дня";s:3:"ask";s:70:"Почему для учащихся 3-х классов не будет ГПД в следующем учебном году?";s:3:"ans";s:84:"Здравствуйте. Из-за недостатка финансирования нет возможности открыть лишнюю группу.";}', 1);
