INSERT INTO `ivschools_pubs_Publications` (`Id`, `User_Id`, `Meta`, `Header`, `Description`, `Content`, `Nicname`, `Status`, `Show`, `Group`, `Pub_Time`) VALUES (3, 3, 'a:4:{s:6:"author";s:10:"Илья Зыкин";s:8:"keywords";s:58:"29 июля запущена Beta версия системы публикаций iv-schools";s:9:"copyright";s:29:"Илья Зыкин, www.iv-schools.ru";s:11:"description";s:58:"29 июля запущена Beta версия системы публикаций iv-schools";}', 'Запуск Beta версии системы публикации', '29 июля запущена Beta версия системы публикаций iv-schools', 'Дорогие друзья и коллеги!\r\n\r\n29 июля 2008 года запущена тестовая версия системы публикации экспериментального социального проекта &quot;Ивановский региональный школьный Интернет Портал&quot; (www.iv-schools.ru).\r\n\r\nК началу нового учебного года администрация сайта постарается закончить доработки и программирование системы публикации.\r\n\r\nМы надеемся, что в новом учебном году учителя, школьники и их родители поддержат идею создания Общественной системы Школьных Публикаций Ивановского региона и обязательно станут активными участниками проекта и авторами интересных школьных новостей.\r\n\r\nПусть о школьных событиях пишут те, кто является участником этих событий!\r\n\r\nС надеждой на плодотворное сотрудничество, администрация сайта www.iv-schools.ru', 'Администратор сайта', '', 1, 1, '1217334694'),
(9, 3, 'a:4:{s:6:"author";s:31:"Зыкин Илья, Администратор сайта";s:8:"keywords";s:50:"Начата разработка раздела &quot;Фотоальбом&quot;";s:9:"copyright";s:10:"Зыкин Илья";s:11:"description";s:50:"Начата разработка раздела &quot;Фотоальбом&quot;";}', 'Новости разработки &quot;Фотоальбом&quot;', 'Начата разработка раздела &quot;Фотоальбом&quot;', '10 августа 2008 года была начата разработка раздела сайта &quot;Фотоальбом&quot;.\r\nФотоальбом Портала будет представлять собой древовидную структуру разделов и альбомов в которых администрация Портала будет размещать фотоматериалы связанные с событиями жизни Ивановских школ.\r\nЗапуск модуля предполагается в воскресенье 17 августа.\r\n\r\nЕсли Вы заинтересованы в размещении фото материалов о жизни Вашей школы в рамках данного Интернет ресурса, то мы предлагаем Вам прислать материалы по следующему адресу электронной почты:\r\n\r\niv-schools@yandex.ru\r\n\r\nПриветствуются фотографии территории школы, фасада, музея школы, учебных аудиторий, школьных мероприятий, учителей и учеников и  самого образовательного процесса.', 'Администратор сайта', 'Администратор', 1, 1, '1218614662'),
(8, 3, 'a:4:{s:6:"author";s:10:"Зыкин И.Н.";s:8:"keywords";s:46:"Ивановский школьный Портал, новости разработки";s:9:"copyright";s:10:"Зыкин И.Н.";s:11:"description";s:40:"Добавлена функция “Похвалить публикацию”";}', 'Новости разработки &quot;Похвалить!&quot;', 'Добавлена функция “Похвалить публикацию”', '2 августа 2008 г. в систему была добавлена функция «Похвалить публикацию». Теперь зарегистрированные на сайте пользователи могут похвалить и отметить понравившуюся им публикацию.\r\nПохвалить новость, можно нажав на ссылку «Похвалить» (рядом с изображением звездочки). Во всплывающем окне подтверждения необходимо нажать кнопку «Да» (т.е. вы подтверждаете, что хотите похвалить публикацию).\r\nСразу после того, как вы выразили свое одобрение, рядом с изображением звездочки количество баллов похвалы возрастет на единицу.\r\nЗа одну публикацию можно проголосовать только один раз.\r\n\r\nP.S.: Интересно, а что будет если похвалить свою публикацию ?! :)', 'Администратор', 'Администратор сайта', 1, 1, '1217663728'),
(10, 3, 'a:0:{}', 'Новости разработки: модуль &quot;Фотоальбом&quot; на стадии тестирования', 'Окончено программирование модуля &quot;Фотоальбом&quot;', 'Согласно намеченному сроку завершена разработка модуля &quot;Фотоальбом&quot;.\r\nМодуль позволяет создавать многоуровневое дерево разделов фотоальбома сайта и альбомы фотографий, как конечные элементы дерева фотоальбома.\r\nСейчас идет тестирование модуля и рассматриваются варианты оформления фотоальбома.\r\nВ ближайшие дни модуль будет запущен в эксплуатацию.', 'Администратор', 'Администратор сайта', 1, 1, '1219044557');
