INSERT INTO `komsomolsk2_Pages` (`Id`, `Description`, `Head`, `Content`) VALUES (1, 'Главное', '', '<h1>Корневой раздел сайта</h1>'),
(2, 'Визитка', 'a:5:{s:5:"title";s:7:"Визитка";s:6:"author";s:55:"МОУ Комсомольская средняя общеобразовательная школа № 2";s:11:"description";s:7:"Визитка";s:8:"keywords";s:55:"МОУ Комсомольская средняя общеобразовательная школа № 2";s:9:"copyright";s:55:"МОУ Комсомольская средняя общеобразовательная школа № 2";}', '&lt;h2&gt;Визитная карточка&lt;/h2&gt;\r\n\r\n\r\n&lt;center&gt;\r\n&lt;img src=&quot;./files/komsomolsk2/common/school.jpg&quot; alt=&quot;Школа&quot; title=&quot;Школа&quot; /&gt;\r\n&lt;/center&gt;\r\n\r\n&lt;br /&gt;&lt;br /&gt;\r\n\r\n&lt;h3&gt;Имя&lt;/h3&gt;\r\n&lt;b&gt;\r\n	Полная средняя общеобразовательная школа №2&lt;br /&gt;&lt;br /&gt;\r\n&lt;/b&gt;\r\n&lt;h3&gt;Дата рождения&lt;/h3&gt;\r\n&lt;b&gt;\r\n	1936 год&lt;br /&gt;&lt;br /&gt;\r\n&lt;/b&gt;\r\n\r\n&lt;h3&gt;Место рождения&lt;/h3&gt;\r\n&lt;b&gt;\r\n	г. Комсомольск Ивановской области,&lt;br /&gt;\r\n	Торговый переулок, дом 8&lt;br /&gt;&lt;br /&gt;\r\n&lt;/b&gt;\r\n\r\n&lt;h3&gt;Директор&lt;/h3&gt;\r\n\r\n\r\n&lt;center&gt;\r\n\r\n&lt;img src=&quot;./files/komsomolsk2/common/direktor.jpg&quot; alt=&quot;Директор школы&quot; title=&quot;Директор школы&quot; /&gt;\r\n\r\n\r\n\r\n&lt;br /&gt;\r\n\r\n&lt;b&gt;\r\nКлевитова Наталья Юрьевна\r\n&lt;/center&gt;\r\n\r\n&lt;br /&gt;&lt;br /&gt;\r\n&lt;/b&gt;\r\n&lt;h3&gt;Общая информация&lt;/h3&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Количество учащихся: 364&lt;/li&gt;\r\n	&lt;li&gt;Количество учителей: 30&lt;/li&gt;\r\n	&lt;li&gt;Количество учебных кабинетов: 22&lt;/li&gt;\r\n	&lt;li&gt;Количество классов комплектов: 16&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;h3&gt;Изучаемые языки&lt;/h3&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;английский&lt;/li&gt;\r\n	&lt;li&gt;французский&lt;/li&gt;\r\n	&lt;li&gt;немецкий&lt;/li&gt;\r\n\r\n&lt;/ul&gt;\r\n\r\n&lt;h3&gt;Контактная информация&lt;/h3&gt;\r\n\r\n&lt;b&gt;Телефон:&lt;/b&gt; (49352)2 - 23 - 72&lt;br /&gt;\r\n&lt;b&gt;e-mail:&lt;/b&gt; sсhol2komsomolsk@mail.ru&lt;br /&gt;\r\n'),
(3, 'Биографическая справка', 'a:5:{s:5:"title";s:22:"Биографическая справка";s:6:"author";s:55:"МОУ Комсомольская средняя общеобразовательная школа № 2";s:11:"description";s:22:"Биографическая справка";s:8:"keywords";s:55:"МОУ Комсомольская средняя общеобразовательная школа № 2";s:9:"copyright";s:55:"МОУ Комсомольская средняя общеобразовательная школа № 2";}', '&lt;p&gt;\r\nСредняя (полная) общеобразовательная школа №2\r\nг. Комсомольска Ивановской области\r\nначала работать в 1936 году,\r\n(была построена за 81 день) как семилетняя,\r\nрассчитанная на 11 классов (600 учащихся).\r\n&lt;/p&gt;\r\n\r\n&lt;p&gt;\r\nДиректором школы был назначен Никитин Николай Иванович,\r\nпогибший в годы Великой Отечественной войны.\r\nПервым завучем школы был Василий Николаевич Евпряков.\r\n21 июня 1941 года 46 учащихся\r\nполучили аттестат о семилетнем образовании.\r\n&lt;/p&gt;\r\n\r\n\r\n&lt;p&gt;\r\nВ годы войны в здании школы размещался госпиталь.\r\nПосле войны школа стала работать как начальная.\r\nЕё руководителем стала Анна Григорьевна Родионова.\r\nВ 1953 году школа была преобразована в среднюю.\r\nПервый выпуск учащихся состоялся в 1957 году.\r\nПродолжателями дела Никитина Н.И. стали директора школы:\r\n&lt;/p&gt;\r\n\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;b&gt;\r\nБрюханов Михаил Макарович  1953г.\r\n	&lt;/b&gt;\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\n	&lt;b&gt;\r\nКузмин Александр Евгеньевич 1953 - 1959г.\r\n	&lt;/b&gt;\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\n	&lt;b&gt;\r\nСоколова Лидия Петровна 1959 - 1963г.\r\n	&lt;/b&gt;\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\n	&lt;b&gt;\r\nАнтипина Юлия Родионовна 1963 - 1965г.\r\n	&lt;/b&gt;\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\n	&lt;b&gt;\r\nКалугин Александр Александрович  1965 - 1968г.\r\n	&lt;/b&gt;\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\n	&lt;b&gt;\r\nЛебедев Вячеслав Николаевич 1968 - 1971г.\r\n	&lt;/b&gt;\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\n	&lt;b&gt;\r\nМясников Валериан Павлович 1971 - 1985г.\r\n	&lt;/b&gt;\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\n	&lt;b&gt;\r\nХрупачева Валентина Андреевна 1985 - 1995г.\r\n	&lt;/b&gt;\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\n	&lt;b&gt;\r\nАверин Владимир Вячеславович 1995 - 1996г.\r\n	&lt;/b&gt;\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\n	&lt;b&gt;\r\nРябова Марина Леонидовна 1996 - 1999г. \r\n	&lt;/b&gt;\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\n	&lt;b&gt;\r\nКурицына Галина Сергеевна 1999 - 2001г.\r\n	&lt;/b&gt;\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\n	&lt;b&gt;\r\nКлевитова Наталья Юрьевна с 2001 года.\r\n	&lt;/b&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n'),
(4, 'Педагоги школы', 'a:5:{s:5:"title";s:3:"МОУ";s:6:"author";s:5:"Автор";s:11:"description";s:14:"Педагоги школы";s:8:"keywords";s:14:"Ключевые слова";s:9:"copyright";s:34:"Авторское право принадлежит автору";}', '&lt;p&gt;\r\n\r\nВ школе работает 30 педагогов. &lt;br /&gt;&lt;br /&gt;\r\n\r\n&lt;h2&gt;Из них имеют:&lt;/h2&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;12 разряд по стажу - 5 педагогов&lt;/li&gt;\r\n	&lt;li&gt;II квалификационную категорию - 2 педагога&lt;/li&gt;\r\n	&lt;li&gt;I квалификационную категорию -  11 педагогов&lt;/li&gt;\r\n	&lt;li&gt;Высшую категорию - 5 педагогов&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;h3&gt;\r\nКурицына Галина Сергеевна имеет 15 разряд и\r\nзвание &quot;Заслуженный учитель России&quot;. \r\n&lt;/h3&gt;\r\n\r\n&lt;/p&gt;\r\n\r\n'),
(5, 'Программы и проекты', 'a:5:{s:5:"title";s:14:"Страница сайта";s:6:"author";s:5:"Автор";s:11:"description";s:19:"Программы и проекты";s:8:"keywords";s:14:"Ключевые слова";s:9:"copyright";s:34:"Авторское право принадлежит автору";}', '&lt;p&gt;\r\nРеализуемые школьные программы и проекты:&lt;br  /&gt;&lt;br  /&gt;\r\n\r\n&lt;b&gt;\r\n&lt;ol&gt;\r\n	&lt;li&gt;Общеобразовательная программа&lt;/li&gt;\r\n	&lt;li&gt;Дети. Интеллект. Творчество&lt;/li&gt;\r\n	&lt;li&gt;Информатизация образования&lt;/li&gt;\r\n	&lt;li&gt;&quot;Здоровье&quot;&lt;/li&gt;\r\n	&lt;li&gt;&quot;Россияночка&quot;&lt;/li&gt;\r\n	&lt;li&gt;&quot;Сокол&quot;. (грант 7 тыс. руб.)&lt;/li&gt;\r\n	&lt;li&gt;&quot;Живая вода&quot;(грант 15 тыс. руб.)&lt;/li&gt;\r\n	&lt;li&gt;&quot;Чтобы помнили&quot;(грант 5 тыс. руб.)&lt;/li&gt;\r\n	&lt;li&gt;&quot;Капелька&quot;&lt;/li&gt;\r\n	&lt;li&gt;Преемственность между дошкольным учреждением и школой.&lt;/li&gt;\r\n	&lt;li&gt;Олимпийские резервы&lt;/li&gt;\r\n	&lt;li&gt;Цветочный рай&lt;/li&gt;\r\n	&lt;li&gt;&quot;Отставала лебедушка прочь от стада лебединого&quot;&lt;/li&gt;\r\n	&lt;li&gt;&quot;Наши родители&quot;(грант 2,5 тыс. руб.)&lt;/li&gt;\r\n&lt;/ol&gt;\r\n&lt;/b&gt;\r\n\r\n&lt;/p&gt;'),
(6, 'Выпускники школы', 'a:5:{s:5:"title";s:16:"Выпускники школы";s:6:"author";s:5:"Автор";s:11:"description";s:16:"Выпускники школы";s:8:"keywords";s:14:"Ключевые слова";s:9:"copyright";s:34:"Авторское право принадлежит автору";}', '\r\n\r\n&lt;p&gt;\r\nНемало интересных людей вышло из стен школы.\r\nОдним из них является Комаров Александр Павлович (1944 г.р.)\r\nВ 1962 году он окончил школу,\r\nучился в летном училище под Рязанью.\r\nЛетал сначала на АН-2 из Иванова в Кострому,\r\nпотом «объездил» всю страну от Калининграда\r\nдо Владивостока и Сахалина.\r\nПосле окончания Академии в Ленинграде\r\nбыл назначен командиром корабля ТУ-154.\r\nЗакончил курсы английского языка и\r\nдевять лет летал за границу.\r\nБыл в Италии, Арабских Эмиратах, Египте,\r\nГермании, Таиланде, Канарских островах.\r\nТеперь живет в Екатеринбурге.\r\n&lt;/p&gt;\r\n\r\n&lt;p&gt;\r\n\r\n&lt;h2&gt;Нашу школу закончили&lt;/h2&gt;\r\n\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\nЛапшин Виктор Михайлович&lt;br /&gt;\r\n(экс заведующий областным управлением образования)\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\nГолубев Владимир Константинович&lt;br /&gt; \r\n(экс глава администрации Комсомольского района)\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\nВострикова Надежда Анатольевна&lt;br /&gt;\r\n(заведующая РОО)\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\nВасильева Татьяна Валентиновна&lt;br /&gt;\r\n(экс директор средней школы №1)\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\nНикитин Владимир Иванович&lt;br /&gt;\r\n(заместитель главы районной администрации)\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\nМокрова Зинаида Венедиктовна&lt;br /&gt;\r\n(врач-окулист)\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\nАндреева Татьяна Александровна&lt;br /&gt;\r\n(врач-педиатр)\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\nКомарова Александра Валентиновна&lt;br /&gt;\r\n(детский врач ЦРБ)\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\nСорокина Ольга Константиновна&lt;br /&gt;\r\n(учитель физкультуры СШ№1)\r\n	&lt;/li&gt;\r\n\r\n	&lt;li&gt;\r\nБольшакова Надежда Анатольевна&lt;br /&gt;\r\n(экс методист дома детского творчества г. Комсомольска)\r\n	&lt;/li&gt;\r\n\r\n&lt;/ul&gt;\r\n\r\n&lt;/p&gt;\r\n'),
(7, 'Конкурсы и проекты', 'a:5:{s:5:"title";s:14:"Страница сайта";s:6:"author";s:5:"Автор";s:11:"description";s:18:"Конкурсы и проекты";s:8:"keywords";s:14:"Ключевые слова";s:9:"copyright";s:34:"Авторское право принадлежит автору";}', '&lt;h2&gt;Районный уровень:&lt;/h2&gt;\r\n\r\nПроект &quot;Отставала лебёдушка...&quot; - IIIместо&lt;br /&gt;\r\nКонкурс &quot;Безопасное колесо&quot; - II место&lt;br /&gt;\r\nКонкурс &quot;Надежда земли Ивановской&quot; - I место&lt;br /&gt;\r\nКонкурс интеллектуалов &quot;Эврика&quot; - I место&lt;br /&gt;\r\n&lt;br /&gt;\r\n\r\n&lt;h2&gt;Областной уровень:&lt;/h2&gt;\r\n\r\nПроект &quot;Наши родители&quot; - победитель конкурса&lt;br /&gt;\r\nИгра &quot;Школа выборов 2006&quot; - благодарственное письмо за активное участие&lt;br /&gt;\r\nМатросов Илья (10 класс) - чемпион области по легкой атлетике&lt;br /&gt;\r\nФролова Анастасия (11класс) - победитель областной олимпиады по обществознанию&lt;br /&gt;\r\n&lt;br /&gt;\r\n\r\n&lt;h2&gt;Всероссийский уровень:&lt;/h2&gt;\r\n\r\nКонкурс &quot;Свой мир строим сами&quot; - лауреаты конкурса&lt;br /&gt;\r\nКонкурс &quot;Человек в истории. Россия - XX век&quot; - благодарственная грамота&lt;br /&gt;\r\n&lt;br /&gt;\r\n\r\n&lt;h2&gt;Международный уровень:&lt;/h2&gt;\r\nПрограмма &quot;Полезная прививка&quot; - благодарственное письмо\r\nза плодотворную экспериментальную работу\r\nпо реализации профилактических программ\r\nмеждународного проекта ХОУП&lt;br /&gt;\r\n&lt;br /&gt;'),
(8, 'Медалисты школы', 'a:5:{s:5:"title";s:14:"Страница сайта";s:6:"author";s:5:"Автор";s:11:"description";s:15:"Медалисты школы";s:8:"keywords";s:14:"Ключевые слова";s:9:"copyright";s:34:"Авторское право принадлежит автору";}', '&lt;h2&gt;Золотые медалисты&lt;/h2&gt;\r\n\r\n&lt;h3&gt;1957&lt;/h3&gt;\r\nЕмельянова Галина Константиновна&lt;br /&gt;&lt;br /&gt;\r\n&lt;h3&gt;1959&lt;/h3&gt;	\r\nИванов Евгений Викторович&lt;br /&gt;&lt;br /&gt;\r\n&lt;h3&gt;1964&lt;/h3&gt;	\r\nЛапшин Виктор Михайлович&lt;br /&gt;&lt;br /&gt;\r\n&lt;h3&gt;1966&lt;/h3&gt;	\r\nБобачева Галина Васильевна&lt;br /&gt;&lt;br /&gt;\r\n&lt;h3&gt;1967&lt;/h3&gt;	\r\nСоколова Лариса Сергеевна&lt;br /&gt;&lt;br /&gt;\r\n&lt;h3&gt;1968&lt;/h3&gt;	\r\nФанина Маргарита Юрьевна&lt;br /&gt;&lt;br /&gt;\r\n&lt;h3&gt;2001&lt;/h3&gt;	\r\nПотемкин Михаил Александрович&lt;br /&gt;&lt;br /&gt;\r\n&lt;h3&gt;2005&lt;/h3&gt;	\r\nМатузнова Елена Валерьевна&lt;br /&gt;&lt;br /&gt;\r\n&lt;h3&gt;2007&lt;/h3&gt;	\r\nСмирнов Василий Николаевич&lt;br /&gt;\r\nФролова Анастасия Алексеевна&lt;br /&gt;&lt;br /&gt;\r\n\r\n&lt;h2&gt;Серебряные медалисты&lt;/h2&gt;\r\n\r\n&lt;h3&gt;1957&lt;/h3&gt;\r\nАбрамова Нина Владимировна&lt;br /&gt;\r\nБасенкова Татьяна Ивановна&lt;br /&gt;\r\nБелова Татьяна Дмитриевна&lt;br /&gt;\r\nБатанова Ирина Федоровна&lt;br /&gt;\r\nКомаров Владимир Александрович&lt;br /&gt;&lt;br /&gt;\r\n\r\n&lt;h3&gt;1958&lt;/h3&gt;	\r\nГолубев Валерий Николаевич&lt;br /&gt;\r\nРусанова Татьяна Васильевна&lt;br /&gt;&lt;br /&gt;\r\n\r\n&lt;h3&gt;1960&lt;/h3&gt;	\r\nБурда Нина Александровна&lt;br /&gt;\r\nКомарова Надежда Ивановна&lt;br /&gt;&lt;br /&gt;\r\n\r\n&lt;h3&gt;1966&lt;/h3&gt;	\r\nКузмичев Александр Анатольевич&lt;br /&gt;&lt;br /&gt;\r\n&lt;h3&gt;1667&lt;/h3&gt;	\r\nСходова Людмила Михайловна&lt;br /&gt;&lt;br /&gt;\r\n&lt;h3&gt;1968&lt;/h3&gt;	\r\nГолова Елена Васильевна&lt;br /&gt;\r\nНовикова Наталья Геннадьевна&lt;br /&gt;&lt;br /&gt;\r\n&lt;h3&gt;1988&lt;/h3&gt;	\r\nКовригина Марина Николаевна&